	public String createPdfFileForCustomer()
	{
		try
		{
			HttpSession sess = request.getSession();
			nom_customer_pdf_path = sess.getAttribute("nom_customer_pdf_path").toString();
			fileName = "";
			
			if(cont_type.trim().equalsIgnoreCase("S"))
			{
				fileName = "CUSTOMER_SELLER_NOM-"+gas_date.trim().substring(0,2)+gas_date.trim().substring(3,5)+gas_date.trim().substring(6)+"-"+cust_abbr+"-"+plant_nm+"-FG"+fgsa_no+"-SN"+sn_ref_no+".pdf";
			}
			else if(cont_type.trim().equalsIgnoreCase("L"))
			{
				fileName = "CUSTOMER_SELLER_NOM-"+gas_date.trim().substring(0,2)+gas_date.trim().substring(3,5)+gas_date.trim().substring(6)+"-"+cust_abbr+"-"+plant_nm+"-TN"+fgsa_no+"-LOA"+sn_ref_no+".pdf";
			}
			else if(cont_type.trim().equalsIgnoreCase("R"))
			{
				fileName = "CUSTOMER_SELLER_NOM-"+gas_date.trim().substring(0,2)+gas_date.trim().substring(3,5)+gas_date.trim().substring(6)+"-"+cust_abbr+"-"+plant_nm+"-RE"+fgsa_no+"-CRG"+sn_no+".pdf";
			}
			else if(cont_type.trim().equalsIgnoreCase("T"))//ADDED FOR LTCORA AND CN
			{
				fileName = "CUSTOMER_SELLER_NOM-"+gas_date.trim().substring(0,2)+gas_date.trim().substring(3,5)+gas_date.trim().substring(6)+"-"+cust_abbr+"-"+plant_nm+"-LTCORA"+fgsa_no+"-CRG"+sn_no+".pdf";
			}
			else if(cont_type.trim().equalsIgnoreCase("C"))//ADDED FOR LTCORA AND CN
			{
				String mapid[]=cont_mapping_id.split("-");
				String ltcora_no=mapid[1];
				fileName = "CUSTOMER_SELLER_NOM-"+gas_date.trim().substring(0,2)+gas_date.trim().substring(3,5)+gas_date.trim().substring(6)+"-"+cust_abbr+"-"+plant_nm+"-LTCORA"+ltcora_no+"-CN"+fgsa_no+"-CRG"+sn_no+".pdf";
			}
			nom_customer_pdf_path = nom_customer_pdf_path+"//"+fileName;
			////System.out.println("fileName = "+fileName+", nom_customer_pdf_path = "+nom_customer_pdf_path);
		}
		catch(Exception e)
		{
			//System.out.println("Exception in createPdfFileForCustomer() Method :\n"+e.getMessage());
			e.printStackTrace();
		}
		return nom_customer_pdf_path;
	}
	
	
